/*
a ui component used to dipslay userlist (boss/pro)
*/
import React from 'react'
import PropTypes from 'prop-types'
import {WingBlank, WhiteSpace, Card} from 'antd-mobile'
import {withRouter} from 'react-router-dom'
import '../../assets/css/list.css'
import QueueAnim from 'rc-queue-anim'

const Header = Card.Header
const Body = Card.Body

class UserList extends React.Component {
  static propTypes = {
    userList: PropTypes.array.isRequired
  }

  render() {
    const {userList} = this.props
    return (
      <WingBlank style={{marginBottom: 50, marginTop: 15}}>
      <QueueAnim type='alpha' delay={100}>
        {
          userList.map(user => (
            <div key={user._id}>
              <WhiteSpace />
              <Card onClick={() => {this.props.history.push(`/chat/${user._id}`)}}>
                <Header
                  thumb={require(`../../assets/sc2avatars/${user.avatar}.png`) }
                  extra={user.username}
                  >
                </Header>
                <Body>
                  <div>SC2 Race：{user.post}</div>
                  {
                    user.company ? <div>Club Name：{user.company}</div> : null
                  }
                  {
                    user.salary ? <div>Salary：{user.salary}</div> : null
                  }
                  <div>Description：{user.info}</div>
                </Body>
              </Card>
            </div>
          ))
        }
        </QueueAnim>
      </WingBlank>
    )
  }
}

export default withRouter(UserList)
