/*
boss info 完善的路由容器组件
*/

import React from 'react'
import {connect} from 'react-redux'

import {
    NavBar,
    InputItem,
    TextareaItem,
    Button
} from 'antd-mobile'
import AvatarSelector from '../../components/avatar-selector/avatar-selector'



class BossInfo extends React.Component {

    state = {
        avatar: '',
        post: '',
        info: '',
        company: '',
        salary: ''
    }

    setAvatar = (avatar) => {
        this.setState({
            avatar
        })
    }

    handleChange = (name, val) => {
        this.setState({
            [name]: val
        })
    }

    save = () => {
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <NavBar> Your Boss Info </NavBar>
                <AvatarSelector setAvatar={this.setAvatar} />
                <InputItem placeholder='race in-need' onChange={val => {this.handleChange('post', val)}}>Race need'd: </InputItem>
                <InputItem placeholder='club Name' onChange={val => {this.handleChange('company', val)}}>Club Name: </InputItem>
                <InputItem placeholder='salary' onChange={val => {this.handleChange('salary', val)}}>Salary: </InputItem>
                <TextareaItem title="Others: "
                                        rows={2} onChange={val => {this.handleChange('info', val)}} />
                <Button type='primary' onClick={this.save}> Save </Button>

            </div>
        )
    }
}



export default connect(
    state => ({}),
    {}
)(BossInfo)
