import {
    AUTH_SUCCESS,
    ERROR_MSG,
    RECEIVE_USER,
    RESET_USER,
    RECEIVE_USER_LIST
} from './Action-types'

import {
    reqRegister,
    reqLogin,
    reqUpdateUser,
    reqUser
} from '../api'




//授权成功的同步 action
const authSuccess = (user) => ({type: AUTH_SUCCESS, data: user})
//错误提示星系的同步action
const errorMsg = (msg) => ({type: ERROR_MSG, data: msg})
// 接收用户的同步action
const receiveUser = (user) => ({type: RECEIVE_USER, data: user})
// 重制用户同步action
export const resetUser = (msg) => ({type: RESET_USER, data: msg})
// 接收用户列表的同步action
export const receiveUserList = (userList) => ({type: RECEIVE_USER_LIST, data: userList})
//注册异步 action
export const register = (user) => {
    const {
        username, 
        password, 
        password2, 
        type
    } = user

    if(!username) {
        return errorMsg('username is a MUST')
    } else if(password !== password2) {
        return errorMsg('ensure SAME password')
    } 

    return async dispatch => {
    // 发送注册的异步请求。
    // 得到promise,通过then链式调用，获取数据
    /* const promise = reqRegister(user)
    promise.then(response => {
        const result = response.data
    }) */
    // 直接获取异步结果
    const response = await reqRegister({
      username,
      password,
      type
    })
    console.log("register -> response", response)
    const result = response.data
    if (result.code === 0) {
      // 成功，分发授权成功的同步action
      console.log(result.data)
      dispatch(authSuccess(result.data))
    } else {
      // 失败，分发错误提示信息的同步action
      dispatch(errorMsg(result.msg))
    }
  }
}




//登陆异步 action
export const login = (user) => {
    const {username, password} = user
    if(!username) {
        return errorMsg('username is a MUST')
    }
    if(!password) {
        return errorMsg('PASSWORD is a MUST')
    }

    return async dispatch => {
        const response = await reqLogin({
          username,
          password
        })
        const result = response.data
        if (result.code === 0) {
          dispatch(authSuccess(result.data))
        } else {
          dispatch(errorMsg(result.msg))
        }
      }
    }

    // updating 异步 action
export const updateUser = (user) => {
  return async dispatch => {
    const response = await reqUpdateUser(user)
    const result = response.data
    if(result.code === 0) {
      dispatch(receiveUser(result.data))
    } else {
      dispatch(resetUser(result.data))
    }
  }
}

// getting user aysn action
export const getUser = () => {
  return async dispatch => {
    const response = await reqUser()
    const result = response.data
    if (result.code === 0) {
      dispatch(receiveUser(result.data))
    } else {
      dispatch(resetUser(result.msg))
    }
  }
}
