/*
boss info 完善的路由容器组件
*/

import React from 'react'
import {connect} from 'react-redux'

import {
    NavBar,
    InputItem,
    TextareaItem,
    Button
} from 'antd-mobile'
import AvatarSelector from '../../components/avatar-selector/avatar-selector'



class BossInfo extends React.Component {

    state = {
        avatar: '',

    }

    handleChange = (name, val) => {
        this.setState({
            [name]: val
        })
    }



    render() {
        return (
            <div>
                <NavBar> Your Boss Info </NavBar>
                <AvatarSelector/>

                <InputItem placeholder='race in-need' onChange={val => {this.handleChange('post', val)}}>Race need: </InputItem>
                <InputItem placeholder='club Name'>Club Name: </InputItem>
                <InputItem placeholder='salary'>Salary: </InputItem>
                <TextareaItem title="Others: "
                                        rows={4} />
                <Button type='primary'> Save </Button>

            </div>
        )
    }
}



export default connect(
    state => ({}),
    {}
)(BossInfo)
